mapper.insert:
insert into course_refund(
    member_no, course_no, deferred_id, original_purchase_no, refund_times, refund_amount,
    refund_channel, refund_reason, operator_id, status, remark
) values (
    #{memberNo}, #{courseNo}, #{deferredId}, #{originalPurchaseNo}, #{refundTimes}, #{refundAmount},
    #{refundChannel}, #{refundReason}, #{operatorId}, #{status}, #{remark}
)

mapper.select:
select * from course_refund


        and member_no = #{memberNo}


order by create_time desc

select coalesce(sum(refund_amount), 0) from course_refund
and member_no = #{memberNo}
and status = 2

select coalesce(sum(refund_amount), 0)
from course_refund
where status = 2
  and create_time >= #{start}
  and create_time <= #{end}
