mapper.select:
SELECT lock_no, venue_no, start_time, end_time, lock_type, remark, status, create_time
FROM venue_slot_lock
WHERE venue_no = #{venueNo}
  AND status = 0
  AND start_time < #{endTime}
  AND end_time > #{startTime}
ORDER BY start_time

mapper.update:
UPDATE venue_slot_lock
SET status = #{status}
WHERE lock_no = #{lockNo}
